import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [numbers, setNumbers] = useState([]);
  const [numberCount, setNumberCount] = useState(100);

  useEffect(()=>{
    for (let index = 0; index < numberCount; index++) {
      setNumbers(numbers => [...numbers, (Math.floor(Math.random() * 100) + 1)]);
    }
  }, []);

  const handleChange = (event) =>{
    setNumberCount(event.target.value);
  };

  const handleClick = (event) =>{
    event.preventDefault();
    setNumbers([]);
    for (let index = 0; index < numberCount; index++) {
      setNumbers(numbers => [...numbers, (Math.floor(Math.random() * 100) + 1)]);
    }
  };

  return (
    <div className='container'>
      <div className="bars">
        {numbers.map(x => <div className='value_bar' style={{flex:x}} key={Math.random()}></div>)}
      </div>
        <form>
          <input onChange={handleChange}></input>
          <button onClick={handleClick}>SET</button>
        </form>
    </div>
  );
}

export default App;
