import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [numbers, setNumbers] = useState([]);
  const [numberCount, setNumberCount] = useState(100);

  const renderBars = () => {
    useEffect(()=>{
      for (let index = 0; index < numberCount; index++) {
        setNumbers(numbers => [...numbers, (Math.floor(Math.random() * 1000) + 1)]);
      }
    }, []);
  }


  const handleChange = (event) =>{
    setNumberCount(event.target.value);
  };

  const handleClick = () =>{
    renderBars();
  };

  return (
    <div className="App">
      {numbers.map(x => <div className='value_bar' style={{width:x}} key={Math.random()}></div>)}
      <form>
        <input onChange={handleChange}></input>
        <button onClick={handleClick}>GO</button>
        </form>
    </div>
  );
}

export default App;
