import './App.css';
import React, { useState, useEffect } from 'react';

const RenderNumbers = (props) => {
  useEffect(()=>{
    for (let index = 0; index < props.numberCount; index++) {
      props.setNumbers(numbers => [...numbers, (Math.floor(Math.random() * 1000) + 1)]);
    }
  }, []);
}

function App() {
  const [numbers, setNumbers] = useState([]);
  const [numberCount, setNumberCount] = useState(100);

  const handleChange = (event) =>{
    setNumberCount(event.target.value);
  };

  return (
    <div className="App">
      {numbers.map(x => <div className='value_bar' style={{width:x}} key={Math.random()}></div>)}
      <form>
        <input onChange={handleChange}></input>
        <button onClick={RenderNumbers} setNumbers={setNumbers}>GO</button>
        </form>
    </div>
  );
}

export default App;
