import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [numbers, setNumbers] = useState([]);
  const [numberCount, setNumberCount] = useState(100);

  useEffect(()=>{
    for (let index = 0; index < numberCount; index++) {
      let element = {"number": (Math.floor(Math.random() * 500) + 1), "selected": false};
      setNumbers(numbers => [...numbers, element]);
      //setNumbers(numbers => [...numbers, (Math.floor(Math.random() * 500) + 1)]);
    }
    console.log(numbers)
  }, []);

  const handleChange = (event) =>{
    setNumberCount(event.target.value);
  };

  const handleSet = (event) =>{
    event.preventDefault();
    setNumbers([]);
    for (let index = 0; index < numberCount; index++) {
      let element = {"number": (Math.floor(Math.random() * 500) + 1), "selected": false};
      setNumbers(numbers => [...numbers, element]);
    }
  };

  const sortFunction = (event) => {
    event.preventDefault();
    let numbers_copy = [...numbers];
    for (let index = 0; index < numbers.length; index++) {
      for (let index2 = 0; index2 < index; index2++) {
        if (numbers_copy[index].number < numbers_copy[index2].number) {
          setTimeout(() => {
            let temp_num = numbers[index].number;
            let temp_num2 = numbers[index2].number;
            console.log(temp_num, temp_num2)
            numbers_copy[index] = temp_num2;
            numbers_copy[index2] = temp_num;
            console.log(index, numbers_copy[index], index2, numbers_copy[index2])
            console.log("----")
            setNumbers(numbers_copy);
            }, 3000);
        }
      }
    }
    //console.log(numbers, sorted)


  };

  return (
    <div className='container'>
      <div className="bars">
        {numbers.map(x => <div className='value_bar' style={{height:x.number}} key={Math.random()}>{x.number}</div>)}
      </div>
        <form>
          <input onChange={handleChange}></input>
          <button onClick={handleSet}>SET</button>
          <button onClick={sortFunction}>SORT</button>
        </form>
    </div>
  );
}

export default App;
